    width: 100%;
    height: max-content;
    position: relative;
    padding: 0 2rem 2rem 2rem; 
`

export const FormContainer = styled.form` 
    display: flex;
    flex-direction: column;
    gap: 1rem;
    margin: 0 auto;
    position: relative;
`
